import defaultArticles from '../fixtures'
import {arrToMap} from './utils'

export default (articles = arrToMap(defaultArticles), action) =>{

  const { type, payload, randomId } = action

  switch (type){


	case 'ADD_TASK':
		  articles = articles.concat(payload.action)
		  break



    default:
        return articles
  }

}
