export const articleMapSelector = (state) => state.articles
export const articleListSelector = createSelector(
	articleMapSelector, 
	(articleMap) => Object.values(articleMap)
)

export const filterSelector = (state) => state.filters.selected

export const filtratedArticlesSelector = (state) =>{
  const {selected} = state.filters

  return state.articles.filter((article) => {
  return(
    (selected !== null)  ? (!selected.length || selected.find((selected) =>
      selected.value === article.id)) : (!selected)
  )})

}
