import defaultArticles from '../fixtures'

export default (articles = defaultArticles, action) =>{

  const { type, payload, randomId } = action

  switch (type){


	case 'ADD_TASK':
		  return {
			  ...articles, [randomId]: {
			  ...payload.article, id: randomId
			  }
		  }

    default:
        return articles
  }

}
