import React, { Component } from 'react'
import ToggleCreate from './toggleCreate'
import ToggleSelect from './toggleSelect'


class Menu extends Component{

	state={
		openedIndex: null
	}



	handleClick = (index) => {
		console.log('open'+ index);
		this.setState({
			openedIndex: this.state.openedIndex === index ? null: index
		})
	}

	render(){

	const {openedIndex} = this.state
	const {handleOpenAdd, handleOpenSelect} = this.props		

	const addI = openedIndex === 0 || openedIndex === null  && 
			 <ToggleSelect onClick={handleOpenSelect}/> 


	const selectI = openedIndex === 1 ||openedIndex ===  null ? 
		  	 <ToggleCreate onClick={handleOpenAdd}/> 
		 : null		  

	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((item, index)=> {
		return( 
				<li key={index} className="col-md-6" 
					onClick={()=>{this.handleClick( index )}}>
					{item}
				</li>
		)}
	)			  

	return(
		<ul className="col-md-12 d-flex flex-row m-0 p-0" >
			{inMenu}
		</ul>
	)}	

}
export default Menu
