import React, { Component } from "react"

class Article extends Component{

  state= {
    green: true
  }

  changeColor(){
      this.setState({black: !this.state.black})
   }
   wrapperFunction = () =>{
     toggleOpen(),
     changeColor()
   }


  render(){
  const {article, toggleOpen} = this.props
  const title = this.state.black ? "blackButton" : "whiteButton";

  return(
  <div>
    <article className="bg-article">
      <div className="container p-0">
        <div className="row p-0 justify-content-md-center">
        <div className="col-md-10 col-12">
          <button onClick={this.wrapperFunction} className={title}>
            <p>{article.title}</p>
          </button>
          { this.getBody() }
        </div>
        </div>
      </div>
    </article>
  </div>
  )
  }

    getBody(){
      const {isOpen, article} = this.props

      if (!isOpen) return null
      return (
        <section className="bg-section">
          <div className="Aricle-location"><p>{article.location}</p></div>
          <div className="Article-text"><p>{article.text}</p></div>
          <div className="Article-member"><p>{article.members}</p></div>
        </section>
      )

    }
}

export default Article
