import {Map} from 'immutable'
import defaultArticles from '../fixtures'
import {arrToMap} from './utils'

export default (articles = new Map(arrToMap(defaultArticles)), action) =>{

  const { type, payload, randomId } = action

  switch (type){


	case 'ADD_ARTICLE':
		  return{
			  ...articles,
			  [randomId]:{ ...payload.article, id:randomId }
		  }

    default:
        return articles
  }

}
