import React, { Component } from 'react'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'
import InfiniteScroll from "react-infinite-scroll-component"


class Scroll extends Component {
  state = {
  //  items: Array.from({ length: 10 }),
    items:[],
    showItems: Array.from({ length: 10 }),
  };

  fetchMoreData = () => {
    setTimeout(() => {
      this.setState({
        showItems: this.state.showItems > this.props.articles ?
         this.state.showItems : this.state.showItems + 10
      });
    }, 1500);
  }


  render() {

    const getItems = this.props.articles.slice(0,this.state.showItems).map((article, key) =>{
      return (
        <li  key={article.id}>
          {article.title}
        </li>)
      })

      console.log(getItems)

    return (
      <div>

        <InfiniteScroll
          dataLength={this.state.items.length}
          next={this.fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >

          {getItems}

        </InfiniteScroll>
      </div>
    );
  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),

  }

})(accordion(Scroll))
