import React, { Component } from 'react'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'
import EventListener, {withOptions} from 'react-event-listener'


class Scroll extends Component {
  state = {
    showItems: 21,
    loading: false
  }

  handleScroll = () => {
      this.setState((state) =>({loading: true}))

      const timer = setTimeout(() => {
        this.setState({
          showItems: this.state.showItems  + 1, loading: false
        });
      }, 1000)
      return () => clearTimeout(timer)
  }

  render() {

    const getItems = this.props.articles.length ? this.props.articles.slice(0,this.state.showItems).map((article, key) =>{
      return (
        <li  key={article.id}>
          {article.title}
        </li>)
      }) : <p>"no"</p>

      console.log(getItems)

    return (
      <div>
        <EventListener
          target="window"
          onResize={this.handleResize}
          onScroll={withOptions(this.handleScroll, {passive: true, capture: false})}
        />
        <ul>
          {getItems}
        </ul>
        {this.state.loading
        ? <p className="App-intro">
           loading ...
         </p>
             : ""}
        <EventListener target={document}/>
      </div>
    )

  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),

  }

})(accordion(Scroll))
