import React, { Component } from 'react'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'


class Scroll extends Component {
  state = {
    showItems: 30
  };

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
}

  handleShowMore = () => {
      this.setState({
        showItems:
        this.state.showItems > this.props.articles ?
        this.state.showItems : this.state.showItems + 10
      })
  }

  render() {

    const getItems = this.props.articles.slice(0,this.state.showItems).map((article, key) =>{
      return (
        <li  key={article.id}>
          {article.title}
        </li>)
      })

      console.log(getItems)

    return (
      <div>
        {getItems}
        <button onClick={this.handleShowMore}>MORE</button>
      </div>
    )
  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),

  }

})(accordion(Scroll))
