import React, { Component } from 'react'
import ToggleCreate from './toggleCreate'
import ToggleSelect from './toggleSelect'


class Menu extends Component{

	state={
		openedIndex: null
	}

	handleClick = (index) => {
		console.log('open'+ index);
		this.setState({
			openedIndex: this.state.openedIndex === index ? null: index
		})
	}

	render(){

	const addI = this.state.openedIndex === 0 || 
		  this.state.openedIndex === null  ? 
		  <div onClick={this.props.handleOpenSelect}>
			  <ToggleSelect/> 
		  </div>: null

	const selectI = this.state.openedIndex === 1 ||
		  this.state.openedIndex ===  null ? 

		  <div onClick={this.props.handleOpenAdd} >
		  	<ToggleCreate/> 
		  </div> : null		  


	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((item, index)=> {
		return( 
				<li key={index} className="" 
					onClick={()=>{this.handleClick(index)}}>
						{item}
				</li>
		)}
	)			  

	return(
		<ul className="col-md-12 d-flex flex-row m-0 p-0" >
			{inMenu}
		</ul>
	)}	

}
export default Menu
