export const filtratedArticlesSelector = (state) =>{
  const {selected} = state.filters
  return state.articles.filter((article) => {
  return(
    (selected !== null)  ? (!selected.length || selected.find((selected) =>
      selected.value === article.id)) : (!selected)
  )})
}
