import React, { Component } from 'react'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'



class Scroll extends Component {
  state = {
    showItems: 20


  }

  handleScroll = () => {
    setTimeout(() => {
    window.scrollY === 0 ? this.setState({ showItems: this.state.showItems}) :
    this.setState({ showItems: this.state.showItems + 1})
  }, 1500)
}

  fetchMoreData = () => {
      setTimeout(() => {
        this.setState({
          items: this.state.items.concat(Array.from({ length: 1 }))
        });
      }, 1500)
    }



  render() {

    const getItems = this.props.articles.slice(0,this.state.showItems).map((article, key) =>{
      return (
        <li  key={article.id}>
          {article.title}
        </li>)
      })

      console.log(getItems)

    return (
      <div>
        {getItems}
      </div>

    )

  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),

  }

})(accordion(Scroll))
