import React, { Component } from 'react'
import Article from './article'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import showArticles from "../decorators/showArticles"
import {filtratedArticlesSelector} from '../selectors'
import EventListener, {withOptions} from 'react-event-listener'


class InfinityList extends Component {


  render() {
    const {articles, openItemId, toggleOpenItem, handleScroll, showItems} = this.props
    const getItems = articles.slice(0,showItems).map((article, key) =>{
      return (
        <li className="article-list-li" key={article.id}>
          <Article
            article={article}
            isOpen = {openItemId === article.id}
            toggleOpen = {toggleOpenItem(article.id)}
            cssColor= {openItemId === article.id}
          />
        </li>
      )
    })

    return (
      <div>
        <EventListener
          target="window"
          onScroll={withOptions(handleScroll, {passive: true, capture: false})}
        />
        <ul className="article-list article-list-bg">
          {getItems}
        </ul>
        <EventListener target={document}/>
      </div>
    )
  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),
  }

})( accordion(InfinityList))
