import React, { Component } from 'react'
import toggleIconDecorator from '../decorators/toggleIconDecorator'
import addArticleIcon from '../create-article.svg'
import selectIcon from '../search-sign.svg'

class Menu extends Component{

	state={
		openedIndex: null
	}

	handleClick = (index) => {
		console.log('open'+ index);
		this.setState({
			openedIndex: this.state.openedIndex === index ? null: index
		})
	}

	render(){

	const { opacity } = this.props
	const styles = {transition: 'all .3s ease-out'}

	const addI = this.state.openedIndex === 0 || this.state.openedIndex === null  ? 
		  <img src={addArticleIcon}/> : null

	const selectI = this.state.openedIndex === 1 ||
		  this.state.openedIndex ===  null ? 

		  <img src={selectIcon}/> 


		  : null

	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((item, index)=> {
		return( 
				<li key={index} className="col-md-6 menu-list-li">
					<button onClick={()=>{this.handleClick( index )}}
						className='icon-style'>
						<div style={{opacity:opacity, ...styles}}>
							{item}
						</div>

					</button>
				</li>
		)}
	)			  

	return(
		<ul className="col-md-12 d-flex flex-row m-0" >
			{inMenu}
		</ul>
	)}	

}
export default toggleIconDecorator(Menu)
