import { Map, fromJS } from 'immutable'

export function arrToMap(arr, DataRecord){
	return arr.reduce(
		(acc, item) => acc.set(item.id, DataRecord ? new DataRecord(item): fromJS(item)),	
		new Map({})
	)
}
