import React from 'react'
import toggleIconDecorator from '../decorators/toggleIconDecorator'
import selectIcon from '../search-sign.svg'

const ToggleSelect =({ toggleAnimation, padding, transformL, transformR, opacity, display, toggleHidden2}) => {

	const styles = {transition: 'all .3s ease-out'}

	return(	

		<button className="icon-style " 
				onClick={()=>{ toggleAnimation(); toggleHidden2() }}
				style={{padding:padding, display: display, ...styles}}
		 >
			  <div style={{transform:transformL, ...styles}} className="devL"></div> 
			  <div style={{transform:transformR, ...styles}} className="devR"></div>
			  <div style={{opacity:opacity, ...styles}}>
				<img src={selectIcon} className='icon-style'  alt="select-icon" />
			  </div>
		</button> 
	)

}
export default toggleIconDecorator(ToggleSelect)
