import { START, SUCCESS, FAIL  } from '../constants'



export default store => next => action => {
  const { callAPI } = action

  if (!callAPI) return next(action)

  fetch(callAPI)
    .then(res => res.json())
    .then(response => next({
        ...action, response
    }))
}
