import React, { Component } from 'react'
import addArticleIcon from '../create-article.svg'
import selectIcon from '../search-sign.svg'

class Menu extends Component{

	state={
		showIcon: false
	}
	openHandler (index) {
		const items = this.state
	}

	render(){

	const addI = <img src={addArticleIcon} />
	const selectI = <img src={selectIcon} />
	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((link, index)=>

		<li key={index}>
			<button onClick={this.openHandler.bind(this,index)} className='icon-style'>
				{link}
			</button>
		</li>
	)			  

	return(
		<ul>
			{inMenu}
		</ul>
	)}	

}
export default Menu
