import defaultArticles from '../fixtures'

export default (articlesState = defaultArticles, action) =>{

  const { type, payload, randomId } = action

  switch (type){

	case 'ADD_ARTICLE':
		  return{
			  ...articlesState,
			  [randomId]:{...payload.article, id:randomId}
		  }



    default:
        return articlesState
  }

}
