import {
  CHANGE_SELECTION, ADD_ARTICLE,
  LOAD_ALL_ARTICLES,
  LOAD_ARTICLE
 } from '../constants'

export default function changeSelection(selected){
  return{
    type: CHANGE_SELECTION,
    payload: { selected }
  }
}


export function loadAllArticles(){
  return{
    type: LOAD_ALL_ARTICLES,
    callAPI: '/api/article'
  }
}
export function addArticle(article){
  return (dispatch) => {
    dispatch({
      type: ADD_ARTICLE,
      payload: {article}
    })
    fetch(`/api/article`)
      .then(res => res.json())
      .then(response => dispatch({
        type: LOAD_ARTICLE,
        payload: {article}
      }))
  }
}

export function loadArticleById(id){
  return (dispatch) => {
    dispatch({
      type: LOAD_ARTICLE,
      payload: {id}
    })
    fetch(`/api/article/${id}`)
      .then(res => res.json())
      .then(response => dispatch({
        type: LOAD_ARTICLE,
        payload: {id}
      }))
  }
}
