import React, {Component} from 'react'
import {connect} from 'react-redux'
import {addArticle} from '../ac'

class AddArticle extends Component{

	state={
		title: '',
		members:'',
		text: '',
	}

	render(){
		return(

		<div className="container-fluid">
			<div className="container">
				<div className="row d-flex justify-content-center">
					<div className="form-container col-md-10 col-12">
						<h3 className="new-article-title">Add new article </h3>

					</div>
				</div>	
			</div>	
		</div>
		)
	}


	handleSubmit = (ev) => {
		ev.preventDefault()
		this.props.addArticle(this.state)
		this.setState({
			title: '',
			members:'',
			text: ''
		})
	}

  	isValidForm = () => ['title', 'members', 'text'].every(this.isValidField)

	isValidField = (type) => this.state[type].length >= limits[type].min

	handleChange = (type) => (ev) =>{
		const {value} = ev.target

		if(value.length>limits[type].max) return
			this.setState({
				[type]: value
			})
	}
}
const limits =  {
	title: { min:2, max:100},
	members: { min:5, max:100},
	text: { min:50, max:350}
}

export default connect(null, (dispatch, ownProps) => ({
	addArticle: (comment) => dispatch(addArticle(comment, ownProps.articleId))
}))(AddArticle)
