import React, { Component } from 'react'
import addArticleIcon from '../create-article.svg'
import selectIcon from '../search-sign.svg'

class Menu extends Component{

	state={
		openedIndex: null
	}

	handleClick = (icon, index) => {

		this.setState({
			openedIndex: index
		})
		//console.log(this.state.openedIndex )
	}


	render(){

	const addI = this.state.openedIndex === 0 || this.state.openedIndex === null  ? 
		  <img src={addArticleIcon}/> : null

	const selectI = this.state.openedIndex === 1 ||
		  this.state.openedIndex ===  null ? 
		  <img src={selectIcon}/> : null

	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((link, index)=> {
		console.log(index);			  
		return <li key={index} className="menu">
				<button onClick={this.handleClick.bind(this, index )}
					className='icon-style'>
					{link}
				</button>
			</li>
		}
	)			  

	return(
		<ul>
			{inMenu}
		</ul>
	)}	

}
export default Menu
