import './App.css'




class App extends Component {

  state = {
  items: Array.from({ length: 20 })
};

  fetchMoreData = () => {
  setTimeout(() => {
    this.setState({
      items: this.state.items.concat(Array.from({ length: 20 }))
    });
  }, 1500);
};

  render(){
    return (
      <div>
        <Header/>
        ArticleList/>



        <Footer/>
      </div>
    )}
}

export default App;
