import React, { Component } from 'react'
import addArticleIcon from '../create-article.svg'
import selectIcon from '../search-sign.svg'

class Menu extends Component{

	state={
		showIcon: null,
		isOpen: true
	}

	handleClick = (index, showIcon) => {
		this.state.isOpen = this.state.showIcon === index

		this.setState({ 
			showIcon: showIcon 
		})
			console.log(showIcon)
	}


	render(){


	const addI = this.state.isOpen  ? 
		  <img src={addArticleIcon}/> : null

	const selectI = this.state.isOpen ? 
		  <img src={selectIcon}/> : null

	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((link, index)=>

		<li key={index} className="menu">
			<button onClick={this.handleClick.bind(this,index)}	
				className='icon-style'>
				{link}
			</button>
		</li>
	)			  

	return(
		<ul>
			{inMenu}
		</ul>
	)}	

}
export default Menu
