import React, { Component } from 'react'
import {connect} from 'react-redux'
import Article from "./article"
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'
import InfiniteScroll from "react-infinite-scroll-component";



class ArticleList extends Component {

  state = {
    items: Array.from({ length: 1 })
  };
  fetchMoreData = () => {
      setTimeout(() => {
        this.setState({
          items: this.state.items.concat(Array.from({ length: 1 }))
        });
      }, 1500);
    };

  render(){
    return(
        <ul className="article-list article-list-bg">
          {this.articleItems}
        </ul>
    )
  }

  get articleItems(){
    const {articles, openItemId, toggleOpenItem} = this.props

    return (
      articles.map(article =>

        <InfiniteScroll
          dataLength={this.state.items.length}
          next={this.fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >
        {this.state.items.map((art) => (

          <div>
            <li className="article-list-li" key={article.id}>

              <Article
                article={article}
                isOpen = {openItemId === article.id}
                toggleOpen = {toggleOpenItem(article.id)}
                cssColor= {openItemId === article.id}
              />

            </li>
          </div>
        ))}

        </InfiniteScroll>

    )
  )

  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state)}

})(accordion(ArticleList))
