import React, {Component} from 'react'
import toggleBtnDecorator from '../decorators/toggleBtnDecorator'
import addArticleSign from '../add-sign.svg'

function BtnAddArticle(props){



	const { toggleAnimation, padding, transformL, transformR, opacity } = this.props	
	const styles = {transition: 'all .3s ease-out'}

	const addArticle_btn = 
		<button className="searchBtn" 
				onClick={ toggleAnimation }
				style={{padding:padding, ...styles}}
		 >
		  <div style={{transform:transformL, ...styles}} className="devL"></div> 

		  <div style={{transform:transformR, ...styles}} className="devR"></div>

		  <div style={{opacity:opacity, ...styles}}>
			<img src={addArticleSign} className='searchSign'  alt="search-sign" />
		  </div>

		</button> 

	return(
		<div>{addArticle_btn}</div>
	)

}
export default toggleBtnDecorator(BtnAddArticle)
