import React, {Component} from 'react'
import {connect} from 'react-redux'
import {addArticle} from '../ac'
import styles from '../App.module.css'
import globalStyles from 'bootstrap/dist/css/bootstrap.module.css'
import classLister from 'css-module-class-lister'

const classes = classLister(styles, globalStyles)

class AddArticle extends Component{

	state={
		title: '',
		members:'',
		text: '',
		submitValue: 'Submit your article',
		errorTitle: '',
		errorMembers: '',
		errorText: ''
	}

render(){

const {title, members, text} = this.state

return(
	<div className="container-fluid p-0 m-0 ">
		<div className="container">
			<div className="row d-flex justify-content-center">
				<div className="form col-md-10 col-12">
					<h3 className="new-article-title">Add new article </h3>
					<form className="add-article-form d-flex flex-column"
							onSubmit={this.handleSubmit}>

						<input className="form-field"
							placeholder="Name of group"
							value={title}
							onChange={this.handleChange('title')}
						/>

						<input className="form-field"
							placeholder="Members"
							value={members}
							onChange={this.handleChange('members')}
						/>

						<input className="form-description"
							placeholder="Description"
							value={text}
							onChange={this.handleChange('text')}
						/>

						<input type='submit' value='SUBMIT YOUR ARTICLE'/>

					</form>
				</div>
			</div>

		</div>
	</div>

)
}



   handleSubmit = (ev) => {

	   ev.preventDefault()

	   const errorMessage =
		   <p className="error_messages">this field can't be blank</p>
	   const errorMessageDescr =
			<p className="error_messages">this field must be at least 50 characters minimum</p>


	   let error = {
	   	 errorTitle: null,
		   errorMembers: null,
		   errorText: null
	   }

	   let hasError = false;

		if (!this.isValidField('title')){
			console.log('hi')
			error.errorTitle = errorMessage
			hasError = true;
		}

	   if (!this.isValidField('members')){
			error.errorMembers = errorMessage
		    hasError = true;

		}
	   	if (!this.isValidField('text')){
			error.errorText = errorMessageDescr
			hasError = true;
		}

	   	this.setState(error)

			if (hasError) {
			   return
			}


		this.props.addArticle(this.state)

		return(
			this.setState({
				title: '',
				members:'',
				text: '',
				submitValue: 'The article is submitted',
			})
		)
   }


   isValidField = (type) => this.state[type].length >= limits[type].min

	handleChange = (type) => (ev) =>{
		const {value} = ev.target
		if(value.length>limits[type].max) return
			this.setState({
				[type]: value
			})
	}
}

const limits =  {
	title: { min:1, max:20},
	members: { min:1, max:100},
	text: { min:5, max:350}
}

export default connect(null, (dispatch, ownProps) => ({
	addArticle: (article) => dispatch(addArticle(article, ownProps.articleId))
}))(AddArticle)
