import React, {Component} from 'react'
import {connect} from 'react-redux'
import {addArticle} from '../ac'

class AddArticle extends Component{

	state={
		title: '',
		members:'',
		text: '',
		submitValue: 'Submit your article',
		isOpenTitle: false,
		isOpenMembers: false,
		isOpenText: false,
	}

	render(){

		const {title, members, text} = this.state

		return(
			<div className="container p-0">
				<div className="row d-flex justify-content-center p-0">
					<div className="form-container col-md-10 col-12">
						<h3 className="new-article-title">Add new article </h3>
						<form className="add-article-form d-flex flex-column" 
							  onSubmit={this.handleSubmit}>

							<input className="form-field"
								placeholder="Name of group"
								value={title}
								onChange={this.handleChange('title')}
							/>
							{this.openErrorsTitle()}

							<input className="form-field"
								placeholder="Members"
								value={members}
								onChange={this.handleChange('members')}
							/>
							{this.openErrorsMembers()}

							<textarea className="form-field-description"
								placeholder="Description"
								value={text}
								onChange={this.handleChange('text')}
							/>
							{this.openErrorsText()}

							<input type='submit' 
								   value={this.state.submitValue}
							/> 

						</form>
					</div>
				</div>	
			</div>	

		)
	}


   openErrorsText = () =>{
	   console.log('text')
		if(!this.state.isOpenText) return(
			<p className="no_error_messages">no errors</p>)
		return(
			<p className="error_messages">Description must be a minimum length of 50 characters.</p>
		)
   }
   openErrorsMembers = () =>{
	   console.log('members')
		if(!this.state.isOpenMembers) return(
			<p className="no_error_messages">no errors</p>)

		return(
			<p className="error_messages">Cannot be blank</p>
		)
   }
   openErrorsTitle = ()=>{
	   console.log('title')
		if(!this.state.isOpenTitle) return (
			<p className="no_error_messages">no errors</p>)

		return(
			<p className="error_messages">Cannot be blank</p>
		)
   }


   handleSubmit = (ev) => {

	 ev.preventDefault()

	   !this.isValidField('title') ? null : this.setState({isOpenTitle:true})





	   if (!this.isValidField('members')){
			return (
				this.setState({
					isOpenMembers:true	
				})
			)
		}
	   	if (!this.isValidField('text')){
			return (
				this.setState({
					isOpenText:true	
				})
			)
		}

		this.props.addArticle(this.state)
		return(
			this.setState({
				title: '',
				members:'',
				text: '',
				isOpenText: false,
				isOpenMembers: false,
				isOpenTitle: false,
				isOpenSuccess: true,
				submitValue: 'The article is submitted',
			}) 
		)   

   }


   isValidField = (type) => this.state[type].length >= limits[type].min

	handleChange = (type) => (ev) =>{
		const {value} = ev.target
		if(value.length>limits[type].max) return
			this.setState({
				[type]: value
			})
	}
}

const limits =  {
	title: { min:1, max:20},
	members: { min:1, max:100},
	text: { min:5, max:350}
}

export default connect(null, (dispatch, ownProps) => ({
	addArticle: (comment) => dispatch(addArticle(comment, ownProps.articleId))
}))(AddArticle)
