import React, { Component } from 'react'
import {connect} from 'react-redux'
import Article from "./article"
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'
import InfiniteScroll from "react-infinite-scroll-component";



class ArticleList extends Component {

  state = {
    articles: Array.from({ length: 4 })
  };
  fetchMoreData = () => {
      setTimeout(() => {
        this.setState({
          items: this.state.articles.concat(Array.from({ length: 1 }))
        });
      }, 1500);
    };

  render(){
    const {articles, openItemId, toggleOpenItem} = this.props
    return(
        <ul className="article-list article-list-bg">
          {this.articleItems}
        </ul>
    )
  }

  get articleItems(){


    return articles.map(article =>
      <li className="article-list-li" key={article.id}>
        <Article
          article={article}
          isOpen = {openItemId === article.id}
          toggleOpen = {toggleOpenItem(article.id)}
          cssColor= {openItemId === article.id}
        />
      </li>
    )
  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state)}

})(accordion(ArticleList))
