import React, { Component } from 'react'
import SelectFilter from './select'
import AddArticle from './addArticle'
import toggleDecorator from '../decorators/toggleDecorator'
import logo from '../logo.svg'
import ToggleCreate from './toggleCreate'
import ToggleSelect from './toggleSelect'
import Menu from './menu'


class Header extends Component {

	state={
		isOpenSection: null,
		isOpenSelect: false,
		isOpenAdd: true
	}

	handleClick (index) {
		this.setState({
			isOpenSection: this.state.isOpenSection === index ? null : index
		})
	}

	toggleOpenSelect = ()=> {
		this.setState({
		  isOpenSelect: !this.state.isOpenSelect
		})
	}
	toggleOpenAdd = () =>{
		this.setState({
		  isOpenAdd: !this.state.isOpenAdd
		})		
	}


 render(){


  return(
    <div>
      <header>
        <div className="container-fluid Bg-container_dark p-0">
          <div className="container">
              <div className="row justify-content-center">
                <div className="col-md-3 Bg-logo">
                  <img src={logo} className='App-logo' alt="logo" />
                </div>
                <div className="col-md-7 d-none d-md-block p-0">
                  <div className="Title">
                    <h1>FOR THE VOICE</h1>
                  </div>
                </div>
              </div>
          </div>
        </div>
        <div className="container-fluid  bg-sub-title">
          <div className="container">
            <div className="row justify-content-center d-flex flex-row">
              <div className="col-md-3 p-0">
                <div className="Logo-years">
                  <p>1910-1920</p>
                </div>
              </div>
              <div className="col-8 d-block d-md-none p-0">
                <div className="Title-sm">
                  <h1>FOR THE VOICE</h1>
                </div>
              </div>
              <div className="col-md-7 col-4 p-0">
                <div className="Sub-title">
                  <h2>AVANGARD RUSSIAN BOOK</h2>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="container-fluid Bg-container_dark section-book-cont p-0">
          <div className="container">
            <div className="row justify-content-center">
              <div className="col-md-9 col-8 Section-book">
                <h3>ASSOCIATIONS, GROUPS AND PUBLISHING HOUSES</h3>
              </div>
			  <div className="col-md-3 col-8 d-flex align-items-center">
				<Menu onButtonClickAdd={this.toggleOpenAdd}

					/>

			  </div>	  
            </div>
			  { this.openSelect }
			  { this.openAdd }
          </div>

        </div>
      </header>
    </div>
  )
 }
get openSelect(){
	return(
		this.state.isOpenSelect  ?  <SelectFilter/> : null
	)	
}
get openAdd(){
	return(
		this.state.isOpenAdd  ?  <AddArticle /> : null
	)	
}



}
export default toggleDecorator(Header)
