import React, { Component } from 'react'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'


class Scroll extends Component {
  state = {
    showItems: 21
  }

  handleScroll = (e) => {
    const element = event.target
    (element.scrollHeight - element.scrollTop === element.clientHeight) ? this.setState({ showItems: this.state.showItems}) :
    this.setState({ showItems: this.state.showItems + 10})
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
  }
  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }



  render() {

    const getItems = this.props.articles.slice(0,this.state.showItems).map((article, key) =>{
      return (
        <li  key={article.id}>
          {article.title}
        </li>)
      })

      console.log(getItems)

    return (
      <div onScroll = {this.handleScroll} >
        {getItems}
      </div>
    )
  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),

  }

})(accordion(Scroll))
