import React, { Component } from 'react'
import {connect} from 'react-redux'
import accordion from "../decorators/accordion"
import {filtratedArticlesSelector} from '../selectors'
import EventListener, {withOptions} from 'react-event-listener'


class Scroll extends Component {
  state = {
    showItems: 20


  }

  handleScroll = () => {
    this.setState({ showItems: this.state.showItems + 1})
  }



  render() {

    const getItems = this.props.articles.slice(0,this.state.showItems).map((article, key) =>{
      return (
        <li  key={article.id}>
          {article.title}
        </li>)
      })

      console.log(getItems)

    return (
      <div>
      <EventListener
        target="window"
        onScroll={withOptions(this.handleScroll, {passive: true, capture: false})}
      />
      <div>
        {getItems}
      </div>
      <EventListener target={document} />
      </div>
    )

  }
}

export default connect((state) => {

  return {articles: filtratedArticlesSelector(state),

  }

})(accordion(Scroll))
