import React, { Component } from 'react'
import {connect} from 'react-redux'
import Article from "./article"
import accordion from "../decorators/accordion"

class ArticleList extends Component {

  render(){

    return(
      <ul className="article-list">
        {this.articleItems}
      </ul>
    )
  }
  get articleItems(){
    const {articles, openItemId, toggleOpenItem, changeColor} = this.props

    return articles.map(article =>
      <li key={article.id}>
        <Article article={article}
          isOpen = {openItemId === article.id}
          toggleOpen = {toggleOpenItem(article.id)}
          changeColor = {changeColor(article.id)}
        />
      </li>
    )
  }
}
export default connect((state) => {
  const {selected} = state.filters

  const filtratedAtricles = state.articles.filter((article) =>{
    return (!selected.length || selected.find((selected) => selected.value === article.id))

  })
  return {
    articles: filtratedAtricles
  }

})(accordion(ArticleList))
