import React from 'react'
export default (OriginalComponent) => class ToggleComponent extends React.Component{

  state={
    isOpenSelect: false,
	isOpenAdd: false  
  }

  handleClickSelect = (id) =>{
    this.setState({
      isOpenSelect: (id='btn-02') ? null: this.state.isOpenSelect
    })
  }

  handleClickAdd = () =>{
    this.setState({
      isOpenAdd: !this.state.isOpenAdd
    })
  }

  render(){
    return <OriginalComponent
      {...this.props}
      isOpenSelect = {this.state.isOpenSelect}
	  isOpenAdd = {this.state.isOpenAdd}

	  handleClickSelect = {this.handleClickSelect}
	  handleClickAdd = {this.handleClickAdd}

    />
  }
}
