import React, {Component} from 'react'
import {connect} from 'react-redux'
import {addArticle} from '../ac'

class AddArticle extends Component{

	state={
		title: '',
		members:'',
		text: ''
	}

	render(){

		return(
			<div className="container p-0">
				<div className="row d-flex justify-content-center p-0">
					<div className="form-container col-md-10 col-12">
						<h3 className="new-article-title">Add new article </h3>
						<form className="add-article-form d-flex flex-column" 
							  onSubmit={this.handleSubmit}>

							<input className="form-field"
								placeholder="Name of group"
								value={this.state.title}
								onChange={this.handleChange('title')}
							/>

							<input className="form-field"
								placeholder="Members"
								value={this.state.members}
								onChange={this.handleChange('members')}
							/>

							<textarea className="form-field-description"
								placeholder="Description"
								value={this.state.text}
								onChange={this.handleChange('text')}
							/>

							<input type='submit' value='SUBMIT YOUR ARTICLE'
							/>   							
						</form>
					</div>
				</div>	
			</div>	

		)
	}


	handleSubmit = (ev) => {
		ev.preventDefault()


		if (!this.isValidForm()){
			console.log('no valid')
			return null
		}
		this.props.addArticle(this.state)
		return(
			this.setState({
			title: '',
			members:'',
			text: ''
			}) 

		)


	}

   isValidForm = () => ['title', 'members', 'text'].every(this.isValidField)

   isValidField = (type) => this.state[type].length >= limits[type].min

   getClassName = (type) => (this.isValidField(type) ? '' : 'form-input__error')


	handleChange = (type) => (ev) =>{
		const {value} = ev.target
		if(value.length>limits[type].max) return
			this.setState({
				[type]: value
			})
	}
}

const limits =  {
	title: { min:1, max:20},
	members: { min:1, max:100},
	text: { min:5, max:350}
}

export default connect(null, (dispatch, ownProps) => ({
	addArticle: (comment) => dispatch(addArticle(comment, ownProps.articleId))
}))(AddArticle)
