import React, { Component } from 'react'
import addArticleIcon from '../create-article.svg'
import selectIcon from '../search-sign.svg'

class Menu extends Component{

	state={
		openedIndex: null
	}

	handleClick = (index) => {
		console.log('open'+ index);
		this.setState({
			openedIndex: this.state.openedIndex === index ? null: index
		})
	}

	render(){

	const addI = this.state.openedIndex ? 
		  <img src={addArticleIcon}/> : null

	const selectI = this.state.openedIndex ? 
		  <img src={selectIcon}/> : null

	const menuItems = [ addI, selectI ]

	const inMenu  = menuItems.map((link, index)=> {

		return <li key={index} className="menu">
					<button onClick={()=>{this.handleClick( index )}}
						className='icon-style'>
						{link}
					</button>
			</li>
		}
	)			  

	return(
		<ul>
			{inMenu}
		</ul>
	)}	

}
export default Menu
