import React from 'react'
import toggleIconDecorator from '../decorators/toggleIconDecorator'
import addArticleIcon from '../create-article.svg'

const ToggleCreate = ({ toggleAnimation, padding, transformL, transformR, opacity,toggleHidden2, display2 , display1 } ) => {

	const styles = {transition: 'all .3s ease-out'}

	return(
		<button className="icon-style"
				onClick={()=>{ toggleAnimation(); toggleHidden2() }}
				style={{padding: padding, display2: display2, display1: display1, ...styles}}
		 >
		  <div style={{transform:transformL, ...styles}} className="devL"></div> 

		  <div style={{transform:transformR, ...styles}} className="devR"></div>

		  <div style={{opacity:opacity, ...styles}}>
			<img src={addArticleIcon} className='icon-style'  alt="search-icon" />
		  </div>

		</button> 

	)

}
export default toggleIconDecorator(ToggleCreate)
