import React from "react"
function Article(props){
  const {article} = props
  return(
  <div>
    <article>
      <div class="container ">
        <div class="row p-o justify-content-start">
        <div class="col-md-9">
          <div class="Article-title"><p>{article.title}</p></div>
          { getBody(props) }
        </div>
        </div>
      </div>
    </article>
  </div>
  )
}
function getBody({isOpen, article}){

  if (!isOpen) return null
  return (
    <section>
      <div class="Aricle-location"><p>{article.location}</p></div>
      <div class="Article-text"><p>{article.text}</p></div>
      <div class="Article-member"><p>{article.members}</p></div>
    </section>
  )

}
export default Article
