import React from 'react'
import toggleBtnDecorator from '../decorators/toggleBtnDecorator'
import selectIcon from '../search-sign.svg'

function ToggleSelect({ toggleAnimation, padding, transformL, transformR, opacity }){

	const styles = {transition: 'all .3s ease-out'}

	return(	

		<button className="searchBtn" 
				onClick={ toggleAnimation }
				style={{padding:padding, ...styles}}
		 >
			  <div style={{transform:transformL, ...styles}} className="devL"></div> 
			  <div style={{transform:transformR, ...styles}} className="devR"></div>
			  <div style={{opacity:opacity, ...styles}}>
				<img src={selectIcon} className='icon-style'  alt="search-sign" />
			  </div>
		</button> 
	)

}
export default toggleBtnDecorator(ToggleSelect)
