import Article from "./article"
import InfiniteScroll from "react-infinite-scroll-component"

class InfinityList extends Component {

  state = {
    items: Array.from({ length: 20 })
  };

  fetchMoreData = () => {
    setTimeout(() => {
      this.setState({
        items: this.state.items.concat(Array.from({ length: 20 }))
      });
    }, 1500);
